//BRUNO GUILHERME LUNARDI//RUAN MICHEL ADABO//IAN MARCELO TOBARpackage controle;import limite.*;public class ControlePrincipal {//abre classe ControlePrincipal    //Controladores    public ControleImovel ctrImovel;    public ControleVenda ctrVenda;    public ControleCorretor ctrCorretor;    //Limites    public LimitePrincipal lmtPrincipal;    public ControlePrincipal() {        //Inicializa os controladores fazendo com que possamos contrala-los        //por meio do controle principal        ctrImovel = new ControleImovel(this);        ctrVenda = new ControleVenda(this);        ctrCorretor = new ControleCorretor(this);        //Inicia o menu principal        lmtPrincipal = new LimitePrincipal(this);    }    //chama a classe RelatorioFaturamento e passa este controlador para o construtor da classe RelatorioFaturamento    public void relatorioFat(){        new RelatorioFaturamento(this);    }    public void relatorioPagto(){        new RelatorioPagamento(this);    }        public void relatorioImovel(){        new RelatorioImovel(this);    }        public void relatorioCorretor(){        new RelatorioCorretores(this);    }        public static void main(String[] args) {        new ControlePrincipal();    }}//fecha classe ControlePrincipal